mapper.select:
select u.user_id as userId, u.user_name as userName, u.user_pwd as userPwd, u.user_gender as userGender, u.user_birthday as userBirthday, u.user_join_date as userJoinDate, u.is_VIP as isVIP,r.role_id as roleId,rr.role_name as roleName from user u left join user_role r on u.user_id = r.user_id left join ( select * from role where is_valid=1 ) rr on r.role_id=rr.role_id
u.is_valid = 1
and u.user_id = #{userId}
and u.is_VIP = #{isVip}
and u.user_name = #{userName}
